select.execute:
var fetch = y.query('select * from html where url=@url and xpath="//pre"', {url : request.url + "?header=0"});

var ysar = fetch.results.pre;
var ysarXML = <></>;
for each (var line in ysar.split("\n")) {
  ysarXML += <line>{line}</line>;
}

response.object = <ysar><line><url>{request.url}</url>{ysarXML}</line></ysar>;
